mapper.update:
UPDATE USER
SET    USERNAME = #{username},
       PASSWORD = #{password},
       PHONE = #{phone}
WHERE SEQ = #{user}

mapper.select:
SELECT HOSPITAL
FROM USER_HOSPITAL_RELATE
WHERE USER = #{user}

SELECT SEQ, USERNAME, PASSWORD, PROVIDER, SOCIAL_ID, ROLE
FROM USER
WHERE SEQ = #{seq}

SELECT SEQ, USERNAME, PASSWORD, PROVIDER, SOCIAL_ID, ROLE
FROM USER
WHERE USERNAME = #{username}

SELECT SEQ, USERNAME, PASSWORD, PROVIDER, SOCIAL_ID, ROLE
FROM USER
WHERE SOCIAL_ID = #{socialId}
  and PROVIDER = #{provider}

SELECT STATUS
FROM USER_STATUS
WHERE USER = #{user}

mapper.update:
UPDATE PATIENT_INFO
SET    NAME = #{name},
       SOCIAL_NUMBER = #{socialNumber},
       RELATE = #{relate}
WHERE SEQ = #{seq}
